let menuIcon = document.querySelector(".navbar__menu");
let menu = document.querySelector(".menu-burger");
let element1 = document.querySelector('.navbar__menu-iconSmall')
let element2 = document.querySelector('.icon-2')
let element3 = document.querySelector('.icon-3')

menuIcon.addEventListener('click',() => {

    menu.classList.toggle('active');
    element1.classList.toggle('active-el1');
    element2.classList.toggle('active-el2');
    element3.classList.toggle('active-el3')
})
